angular.module("ngStorage",[]).service("storage",function(){var version=1;function set(key,value){try{return localStorage.setItem(key,angular.toJson(value))}catch(e){throw new Error("Storage set error for key: "+key)}}function get(key){try{return angular.fromJson(localStorage.getItem(key))}catch(e){throw new Error("Storage get error for key: "+key)}}function remove(key){return localStorage.removeItem(key)}if(get("v")!==version){localStorage.clear();set("v",version)}return{get:get,set:set,remove:remove}});